#define HTMLDATA_WIFISETUP_INDEX_HTML "<!DOCTYPE html>\n<html lang=\"ja\">\n<head>\n<meta charset=\"utf-8\">\n<title>ESP8266</title>\n<meta name=\"viewport\" content=\"width=device-width\">\n\n<style>\nbody {\n	font-size: 12px;\n}\ntable {\n	border-collapse: collapse;\n}\n\ntable td {\n	border: 1px solid #000000;\n	padding: 10px;\n}\n</style>\n\n</head>\n<body>\n<h1>接続先WiFiの設定</h1>\n<form method=\"POST\" action=\"/\">\n\n<table>\n<tr><td rowspan=\"3\">接続先1</td></tr>\n<tr><td>SSID</td><td><input type=\"text\" name=\"ssid1\" size=\"20\" maxlength=\"32\"></td></tr>\n<tr><td>PSK</td><td><input type=\"password\" name=\"passphase1\" size=\"20\" maxlength=\"64\"></td></tr>\n\n<tr><td rowspan=\"3\">接続先2</td></tr>\n<tr><td>SSID</td><td><input type=\"text\" name=\"ssid2\" size=\"20\" maxlength=\"32\"></td></tr>\n<tr><td>PSK</td><td><input type=\"password\" name=\"passphase2\" size=\"20\" maxlength=\"64\"></td></tr>\n\n<tr><td rowspan=\"3\">接続先3</td></tr>\n<tr><td>SSID</td><td><input type=\"text\" name=\"ssid3\" size=\"20\" maxlength=\"32\"></td></tr>\n<tr><td>PSK</td><td><input type=\"password\" name=\"passphase3\" size=\"20\" maxlength=\"64\"></td></tr>\n\n<tr><td rowspan=\"3\">接続先4</td></tr>\n<tr><td>SSID</td><td><input type=\"text\" name=\"ssid4\" size=\"20\" maxlength=\"32\"></td></tr>\n<tr><td>PSK</td><td><input type=\"password\" name=\"passphase4\" size=\"20\" maxlength=\"64\"></td></tr>\n<tr><td colspan=\"3\"><input type=\"submit\" value=\"設定\"></td></tr>\n</table>\n\n</form>\n</body>\n</html>\n"
#define HTMLDATA_WIFISETUP_INDEX_POST_HTML "<!DOCTYPE html>\n<html lang=\"ja\">\n<head>\n<meta charset=\"utf-8\">\n<title>ESP8266</title>\n<meta name=\"viewport\" content=\"width=device-width\">\n\n<style>\nbody {\n	font-size: 12px;\n}\ntable {\n	border-collapse: collapse;\n}\n\ntable td {\n	border: 1px solid #000000;\n	padding: 10px;\n}\n</style>\n\n</head>\n<body>\n\n<h1>接続先WiFiの設定</h1>\n<p>設定が完了しました</p>\n<p><a href=\"/\">戻る</a></p>\n\n</body>\n</html>\n"

